import React from 'react';

class Form extends React.Component {
  constructor() {
    super();

    this.handleChangeText = this.handleChangeText.bind(this);
    this.state = {
      name: '',
      lastName: '',
      checkbox: false,
    }
  }

  handleChangeText({target}) {
    this.setState({
      name: target.name.value,
      lastName: target.lastName.value,
    })
  }



  render() {
    return (
      <>
        <form>
          <input name="name" type="text" value={this.state.name.value} onChange={this.handleChangeText} />
          <input name="lastName" type="text" value={this.state.lastName.value} onChange={this.handleChangeText} />
          <input name="checbox" type="checkbox" />
        </form>
      </>
    )
  }
}

export default Form;
