import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      person: '',
    }
  }

  componentDidMount() {
    this.conectApi();
  }

  conectApi = async () => {
    const url = 'https://api.randomuser.me/';
    fetch(url)
      .then((response) => response.json())
      .then((data) => {

        this.setState({
          person: data.results,
        })

      })
  }

  render() {
    return (
      console.log('Olá')
    )
  }
}  

export default App;
