        {
        (this.props.email === '' && '')
        (verifyEmail(email) === undefined ? 'Email Inv치lido' : 'Email V치lido')

        }
      </h3>
    </div>
  );
};

ValidEmail.propTypes = {
  email: PropTypes.string.isRequired,
};

