export const getImage = (json) => {
  return { type: GET_IMAGE, payload: json.message };
}

export const requestDog = () => {
  return { type: REQUEST_IMAGE };
}

export const failedRequest = (error) => {
  return { type: FAILED_REQUEST, payload: error };
}
